Talkio.Classes.AsidePanel = Backbone.View.extend({
  hide: function () {
    this.$el.removeClass('show');
  },

  toggle: function () {
    this.$el.toggleClass('show');

    // checking if panel is now visible or not
    if (this.$el.is(':visible')) {
      this.onShow();
      $(document).on('click', _.bind(this.hideOnBlur, this));
    } else {
      this.onHide();
      $(document).off('click', _.bind(this.hideOnBlur, this));
    }
  },

  hideOnBlur: function (event) {
    // closing setting only if the click was made on a target outside of the #user-settings
    if (this.$el.find(event.target).length == 0) {
      this.$el.removeClass('show');

      // unbinding this event
      $(document).off(event);
    }
  },

  // Callbacks to be overriden in child classes
  onShow: function () {},
  onHide: function () {}
});
