Talkio.Models.Settings = Backbone.Model.extend({
  defaults: {
    sound_enabled: false,
    notifications_enabled: false,
    notifications_supported: false,
    current_theme: 'theme--day',
    previous_theme: null
  },


  initialize: function () {
    if ($.cookie('theme')) { this.set('current_theme', $.cookie('theme')); }
    if ($.cookie('sound_enabled')) { this.set('sound_enabled', true); }

    // checking if OS notifications are supported
    if (notify.isSupported) {
      this.set('notifications_supported', true);
      if ($.cookie('notifications_enabled')) {
        // permissions might have changed since the cookie was set
        if (notify.permissionLevel() == 'granted') {
          this.set('notifications_enabled', true);
        }
      }
    }
  },

  toggleSound: function () {
    if (this.isSoundEnabled()) {
      this.set('sound_enabled', false);
      $.removeCookie('sound_enabled');
    } else {
      this.set('sound_enabled', true);
      $.cookie('sound_enabled', 1, { path: '/' });

      Talkio.Variables.chat_room.playSound();
    }
  },

  toggleNotifications: function () {
    if (this.get('notifications_enabled')) {
      this.set('notifications_enabled', false);

      $.removeCookie('notifications_enabled');
    } else {
      notify.requestPermission(_.bind(function () {
        if (notify.permissionLevel() == 'granted') {
          this.set('notifications_enabled', true);
          $.cookie('notifications_enabled', 1, { path: '/' });

          Talkio.Variables.chat_room.displayNotification('Notifications are enabled', '');
        }
      }, this));
    }
  },

  setTheme: function (new_theme) {
    if (this.get('current_theme') != new_theme) {
      this.set('previous_theme', this.get('current_theme'));
      this.set('current_theme', new_theme);

      $.cookie('theme', this.get('current_theme'));
    }
  },

  isSoundEnabled: function () {
    return this.get('sound_enabled');
  },

  areNotificationsSupported: function () {
    return this.get('notifications_supported');
  },

  areNotificationsEnabled: function () {
    return this.get('notifications_supported') && this.get('notifications_enabled');
  }
});
