Talkio.Views.ChatRoom = Backbone.View.extend({
  el: 'body',

  events: {
    'click #scroll_down'    : 'scrollToBottom',
    'click #panel-button'   : 'togglePanel',
    'click .warning button' : 'closeWarning'
  },

  window_is_inactive: false,
  inactive_since: null,
  inactive_messages: 0,
  remove_unread_timeout: null,
  scroll_timeout: null,
  view_at_the_bottom: true,
  is_iframe: false,
  is_offline: false,

  sound: document.getElementById('audio_new'),


  initialize: function () {
    if (window != window.top) { this.is_iframe = true; }

    Talkio.Variables.current_user.on('currentUserChanged', this.changeBodyClass, this);

    // Models
    Talkio.Variables.settings = new Talkio.Models.Settings();

    // Collections
    Talkio.Variables.messages = new Talkio.Collections.Messages();

    // Particiapnt list && banned users
    Talkio.Variables.banned_users = new Talkio.Views.BannedUsers({ collection: this.collection }).render();

    // Page elements
    Talkio.Variables.header = new Talkio.Views.Header({ collection: Talkio.Variables.owners }).render();
    Talkio.Variables.aside  = new Talkio.Views.Aside({ collection: this.collection }).render();
    Talkio.Variables.footer = new Talkio.Views.Footer({ model: Talkio.Variables.current_user }).render();
    Talkio.Variables.live   = new Talkio.Views.Messages({ el: '#live', collection: Talkio.Variables.messages });

    // Cached jQuery elements
    Talkio.Variables.$window   = $(window);
    Talkio.Variables.$document = $(document);

    Talkio.Variables.$header = $('header');
    Talkio.Variables.$footer = $('footer');
    Talkio.Variables.$aside  = $('aside');

    this.$load_messages_button = $('#load-button');
    this.$scroll_down_arrow =    $('#scroll_down');
    this.$message_input =        $('#message_body');

    // DOM & Data events
    Talkio.Variables.messages.on('add', this.notifyUser, this);

    Talkio.Variables.owners.on('add', this.changeBodyClass, this);
    Talkio.Variables.owners.on('remove', this.changeBodyClass, this);

    Talkio.Variables.$window.on('blur', _.bind(this.onWindowBlur, this))
                            .on('focus', _.bind(this.onWindowFocus, this))
                            .on('scroll', _.bind(this.onWindowScroll, this))
                            .on('beforeunload', _.bind(this.onWindowBeforeUnload, this));

    // Calling onWindowFocus when all the messages are rendered
    Talkio.Variables.live.on('rendered', this.onWindowFocus, this);

    this.changeBodyClass();
    this.readInactivityTimestamp();
    this.configureWarning();

    // Fetching chat messages from the server
    Talkio.Variables.messages.fetch({ reset: true });

    // Connecting to Faye server
    Talkio.Variables.socket = new Socket();
  },

  changeBodyClass: function () {
    this.$el.removeClass('isHero isTroll isVirgin');

    if (Talkio.Variables.current_user.isSignedIn()) {
      this.$el.addClass(Talkio.Variables.current_user.fromTwitter() ? 'isHero' : 'isTroll');
    } else {
      this.$el.addClass('isVirgin');
    }

    if (Talkio.Variables.owners.length > 0) {
      this.$el.addClass('hasOwner');
    } else {
      this.$el.removeClass('hasOwner');
    }
  },

  // TODO: Not sure if this is the right place, but it seems to work.
  togglePanel: function (event) {
    $('body').toggleClass('showPanel');
  },

  softScrollToBottom: function () {
    if (this.view_at_the_bottom) {
      this.scrollToBottom();
    } else {
      this.$scroll_down_arrow.addClass('new');
    }
  },

  scrollToBottom: function (event) {
    if (typeof event != undefined) {
      this.$scroll_down_arrow.removeClass('show');
    }

    Talkio.Variables.$window.scrollTop(Talkio.Variables.$document.height());
  },

  scrollToTop: function () {
    Talkio.Variables.$window.scrollTop(0);
  },

  // Message notifications
  updateWindowTitle: function () {
    Tinycon.setBubble(this.inactive_messages);
  },

  playSound: function () {
    if(Talkio.Variables.settings.isSoundEnabled()) {
      try {
        this.sound.play();
      } catch(e) {
        // console.log('no audio');
      }
    }
  },

  showNotification: function (message) {
    if (Talkio.Variables.settings.areNotificationsEnabled()) {
      this.displayNotification(message.plainBody(), 'from ' + message.get('nickname'));
    }
  },

  displayNotification: function (text, signature) {
    var notification = notify.createNotification(text, { icon: '/images/logo.png', body: signature });
    // notification.show();

    setTimeout(function () { notification.close(); }, 3000);
  },

  notifyUser: function (message) {
    var user = Talkio.Variables.users.get(message.get('user_token'));

    if (this.window_is_inactive && !user.isBanned()) {
      this.inactive_messages += 1;
      this.updateWindowTitle();
      this.playSound();
      this.showNotification(message);
    }
  },

  // Window Events
  onWindowBlur: function (event) {
    this.window_is_inactive = true;
    this.saveInactivityTimestamp();

    clearTimeout(this.remove_unread_timeout);
  },

  onWindowFocus: function (event) {
    this.window_is_inactive = false;
    this.inactive_messages = 0;
    this.updateWindowTitle();

    if (this.view_at_the_bottom && !this.is_iframe) {
      this.$message_input.focus();
    }

    this.resetInactivityTimestamp();

    this.remove_unread_timeout = setTimeout(function () {
      $('#chat dd.unread').removeClass('unread');
    }, 4000);
  },

  onWindowBeforeUnload: function (event) {
    this.saveInactivityTimestamp();
    Talkio.Variables.socket.disconnect();
  },

  onWindowScroll: function (event) {
    // Using a timeout to avoid calculating document and window dimensions on every scroll event
    clearTimeout(this.scroll_timeout);
    this.scroll_timeout = setTimeout(_.bind(function () {
      if((Talkio.Variables.$window.scrollTop() + 120) >= (Talkio.Variables.$document.height() - Talkio.Variables.$window.height())) {
        this.$scroll_down_arrow.removeClass('show').removeClass('new');
        this.view_at_the_bottom = true;
      } else {
        this.$scroll_down_arrow.addClass('show');
        this.view_at_the_bottom = false;
      }

      // TODO: Optimize "html" selector
      if((Talkio.Variables.$window.scrollTop() + 10) >= (Talkio.Variables.$document.height() - Talkio.Variables.$window.height())) {
        $('html').removeClass('isAtBottom');
      } else {
        $('html').addClass('isAtBottom');
      }

    }, this), 200);
  },

  // Opening twitter auth in popUp only for embeded tlk.io to remain on the original page
  authWithTwitter: function () {
    var path = '/auth/twitter?origin=' + encodeURIComponent('/' + Talkio.Variables.permalink);

    if (this.is_iframe) {
      this.openPopupWindow(path, 'Login with Twitter', 500, 400);
    } else {
      window.location.href = path;
    }
  },

  openPopupWindow: function (url, title, width, height) {
    window.open(url, title, 'width=' + width + ', height=' + height + ', modal=no, resizable=no, toolbar=no, menubar=no, scrollbars=no, alwaysRaise=yes');
  },

  // managing the inactivity timestamp

  getInactivityTimestamp: function () {
    // fetching variable with a help of the method, because of a strange bug, when the variable is set, but not available from the outside
    return this.inactive_since;
  },

  readInactivityTimestamp: function () {
    if ($.cookie('inactive_since')) { this.inactive_since = parseInt($.cookie('inactive_since'), 10); }
  },

  saveInactivityTimestamp: function () {
    this.inactive_since = Math.round((new Date().getTime()) / 1000);
    $.cookie('inactive_since', this.inactive_since, { path: window.location.pathname });
  },

  resetInactivityTimestamp: function () {
    this.inactive_since = null;
    $.removeCookie('inactive_since', { path: window.location.pathname });
  },

  configureWarning: function () {
    if (!$.cookie('warning_was_closed')) {
      this.$('.warning').removeClass('is-hidden');
    }
  },

  closeWarning: function (event) {
    event.preventDefault();

    $.cookie('warning_was_closed', true, { path: '/' });
    this.$('.warning').addClass('is-hidden');
  }
});
Talkio.Views.Footer = Backbone.View.extend({
  el: 'footer',
  events: {
    'submit #participant-form'    : 'joinAsTroll',
    'submit #new_message'         : 'submitMessage',

    'click #twitter-button'       : 'joinAsHero',
    'click #join-toggleButton'   : 'toggleJoin',

    'keydown #message_body'       : 'messageKeydown',
    'keyup #message_body'         : 'messageKeyup',
    'focus #message_body'         : 'messageFocus',
    'blur  #message_body'         : 'messageBlur'
  },

  message_body_overflow: 0, // used this so the CSS doesn't get applied on every keydown.
  scroll_height_padding: 0,

  $message_body: null,
  $footer_avatar: null,
  $submit_button: null,


  initialize: function () {
    this.model.on('currentUserChanged', this.render, this);
  },

  render: function () {
    if (this.model.isSignedIn()) {
      this.model.on('change:nickname', this.changePlaceholder, this);

      this.$el.html(Talkio.Templates.message_form(this.model.attributes));

      this.$message_body = this.$('#message_body');
      this.$footer_avatar = this.$('#footer-avatar');
      this.$submit_button = this.$('#submit-button');

      this.changePlaceholder();

      // hack because FF doesn't add the padding to scrollHeight
      if (this.$message_body[0].scrollHeight < 50) {
        this.scroll_height_padding = 22;
      }
    } else {
      this.$el.html(Talkio.Templates.join_chatroom());
    }

    return this;
  },

  changePlaceholder: function () {
    this.$message_body.attr('placeholder', this.model.placeholder());
  },
  
  toggleJoin: function (event) {
    event.preventDefault();
    this.$('#join-toggleButton').css( { 'display': 'none' } );
    this.$('#join').css( { 'display': 'block' } );
    Talkio.Variables.chat_room.scrollToBottom();
  },
  
  joinAsTroll: function (event) {
    event.preventDefault();

    this.model.save({ nickname: this.$('#participant_nickname').val() }, { wait: true });
  },

  joinAsHero: function (event) {
    event.preventDefault();

    Talkio.Variables.chat_room.authWithTwitter();
  },

  messageKeydown: function (event) {
    var message_lenth = this.$message_body.val().length;

    if (event.keyCode == 13 && !event.shiftKey) {
      event.preventDefault();

      this.submitMessage();

      return false;
    }
  },

  messageKeyup: function (event) {
    var _rotate;

    if (event.keyCode != 13 || event.shiftKey) {
      var message_lenth = this.$message_body.val().length;

      if (message_lenth > 10) {
        this.model.setTyping();
      } else if (message_lenth == 0) {
        this.model.setCancelledTyping();
      }

      var overflow = this.$message_body[0].scrollHeight - 50;
      if (overflow != this.message_body_overflow) {
        this.$message_body[0].style.height = 'auto';
        overflow = this.$message_body[0].scrollHeight - 50;
        this.message_body_overflow = overflow;
        this.$message_body.css({ height: (50 + this.message_body_overflow + this.scroll_height_padding) + 'px' });
        Talkio.Variables.chat_room.scrollToBottom();
      }

      // rotate avatar while typing
      _rotate = 'rotate(' + message_lenth + 'deg)';
    } else {
      // clear avatar rotation
      _rotate = 'rotate(0deg)';
    }

    this.$footer_avatar.css({ 'transform': _rotate });
  },

  messageFocus: function (event) {
    this.$el.addClass('hasFocus');

    // due to the "tab-index", this event occures before the class is initialized properly
    if (this.$submit_button)
      this.$submit_button.removeClass('show');

    // hide nickname while typing to save space
    if (Modernizr.touch) {
      Talkio.Variables.$aside.addClass('removed');
      Talkio.Variables.$header.addClass('removed');
    }
  },

  messageBlur: function (event) {
    this.$el.removeClass('hasFocus');

    if (this.$message_body.val().length > 0) {
      this.$submit_button.addClass('show');
    }

    if (Modernizr.touch) {
      Talkio.Variables.$aside.removeClass('removed');
      Talkio.Variables.$header.removeClass('removed');

      Talkio.Variables.chat_room.scrollToBottom();
    }
  },

  replyToUsername: function (user_name) {
    if (user_name) {
      var message_value = this.$message_body.val(),
          reply_text = '@' + user_name + ' ';

      // inserting leading space for text which is non-empty and without trailing space
      if (message_value.length > 0 && message_value[message_value.length - 1] != ' ') { this.$message_body.val(message_value + ' '); }

      this.$message_body.val(this.$message_body.val() + reply_text).focus();
      this.$message_body[0].selectionStart = this.$message_body[0].selectionEnd = this.$message_body.val().length;

      Talkio.Variables.chat_room.scrollToBottom();
    }
  },

  submitMessage: function (event) {
    if (event) { event.preventDefault(); }

    var message_lenth = this.$message_body.val().length;

    if (message_lenth > 0) {
      var message = new Talkio.Models.Message({ body: this.$message_body.val() });
      message.save();

      this.model._typing = false;

      this.$submit_button.removeClass('show');
      this.$message_body.val('').attr({ style: '' });
      this.message_body_overflow = 0;

      // clear avatar rotation
      this.$footer_avatar.css({ 'transform': 'rotate(0deg)' });
    }
  }
});
