Talkio.Views.Message = Backbone.View.extend({
  template: Talkio.Templates.message,
  tagName: 'dd',

  events: {
    'click  a.reply'               : 'reply',
    'click  img'                   : 'toggleImage',
    'mouseenter .post-time'        : 'showTimestamp',
    'click      .js-post-time-tag' : 'toggleTimestamp',
    'click #delete-message'        : 'deleteMessage'
  },

  timestamp_format: null,
  $delete_button: null,


  initialize: function () {
    Talkio.Variables.current_user.on('ownership_changed', this.configureOwner, this);
    this.model.on('change:deleted', this.markAsDeleted, this);
  },

  reply: function (event) {
    event.preventDefault();
    Talkio.Variables.footer.replyToUsername($(event.target).data('reply-to'));
  },

  toggleImage: function (event) {
    $(event.target).toggleClass('expanded');
  },

  markAsDeleted: function () {
    if (this.model.isDeleted()) {
      this.$el.addClass('deleted').html(this.model.get('body'));
    }
  },

  configureOwner: function () {
    if (this.model.canBeDeletedBy(Talkio.Variables.current_user)) {
      this.$delete_button.show();
    } else {
      this.$delete_button.hide();
    }
  },

  showTimestamp: function (event) {
    this.$('.js-post-time-tag').text($.timeago(this.model.createdAt()));
    this.timestamp_format = 'relative';
  },

  toggleTimestamp: function (event) {
    if (this.timestamp_format == 'relative') {
      this.$('.js-post-time-tag').text(this.model.createdAt().toLocaleString());
      this.timestamp_format = 'absolute';
    } else {
      this.showTimestamp();
    }
  },

  deleteMessage: function () {
    if (this.model.canBeDeletedBy(Talkio.Variables.current_user)) {
      this.model.delete();
    }
  },

  render: function () {
    var message_body = this.model.processedBody();

    if (Talkio.Variables.current_user.isSignedIn()) {
      // Adding 'is-reply' class for all messages containing @<username>
      var regexp = new RegExp('>@' + Talkio.Variables.current_user.get('nickname') + '<', 'i');
      if (message_body.match(regexp)) {
        this.$el.addClass('is-reply');
      }
    }

    this.$el.addClass('post-message')
            .attr('data-timestamp', this.model.get('timestamp'))
            .html(this.template({ body : message_body }));

    // adding an image for image links.
    this.$('a[data-type="image"]').each(_.bind(function (index, element) {
      this.$el.prepend('<img class="post-img" src="' + $(element).attr('href') + '" />');
    }, this));

    // "animate" class triggers slide and fade animation, doesn't need to be removed
    if (this.model.justAdded() && !Talkio.Variables.chat_room.window_is_inactive) { this.$el.addClass('animate'); }
    if (!this.model.isReadByCurrentUser()) { this.$el.addClass('unread'); }

    this.$delete_button = this.$('#delete-message');

    this.configureOwner();
    this.markAsDeleted();

    return this;
  }
});
