Talkio.Views.OnlineUser = Backbone.View.extend({
  tagName: 'li',

  events: {
    'click' : 'toggleBan'
  },

  initialize: function () {
    this.model.on('remove', this.remove, this);
    this.model.on('new_message', this.moveToTop, this);
    this.model.on('change:nickname', this.rename, this);
    this.model.on('change:is_typing', this.setTyping, this);
  },

  // 'muting' a user by adding a CSS rule. All rules are regenerated on each 'mute'
  toggleBan: function (event) {
    if (!Talkio.Variables.users.isCurrentUser(this.model.id)) {
      this.$el.toggleClass('muted');

      if (this.model.isBanned()) {
        Talkio.Variables.banned_user_ids = _.without(Talkio.Variables.banned_user_ids, this.model.id);
        this.model.trigger('removeBannedUser', this);
      } else {
        Talkio.Variables.banned_user_ids.push(this.model.id);
        this.model.trigger('addBannedUser', this);
      }

      $.cookie('ban_list', Talkio.Variables.banned_user_ids.join(','), { expires: 7, path: window.location.pathname });

      Talkio.Variables.chat_room.scrollToBottom();
    };
  },

  moveToTop: function () {
    this.$el.removeClass('updated')
            .detach()
            .removeClass('typing')
            .prependTo('#online-participants')
            .addClass('updated');
  },

  setTyping: function () {
    if (this.model.isTyping()) {
      this.$el.addClass('typing');
    } else {
      this.$el.removeClass('typing');
    }
  },

  rename: function (user) {
    this.$el.html(user.get('nickname'));
  },

  render: function () {
    this.$el.attr('id', 'author_' + this.model.id)
            .addClass('list-item')
            .html(Talkio.Templates.online_user(this.model.attributes));

    if (this.model.isBanned()) { this.$el.addClass('muted'); }

    return this;
  }
});
