var Authenticator = {
  outgoing: function (message, callback) {
    if (message.channel == '/meta/subscribe') {
      if (!message.ext) message.ext = {};

      message.ext.token = Talkio.Variables.current_user.id;
    }

    callback(message);
  }
}


var Socket = function () {
  this.initialize.apply(this, arguments);
};


_.extend(Socket.prototype, {
  _faye_client: null,
  _socket_notifications: null,
  _private_subscription: null,
  _public_subscription: null,

  initialize: function () {
    this._socket_notifications = new SocketNotifications();

    // Setting up Faye client
    this._faye_client = new Faye.Client(Talkio.Variables.faye_server, { timeout: 60 });
    this._faye_client.addExtension(Authenticator);

    this._faye_client.on('transport:up', function () {
      if (Talkio.Variables.chat_room.is_offline) {
        Talkio.Variables.chat_room.is_offline = false;
      }
    });

    this._faye_client.on('transport:down', function () {
      Talkio.Variables.chat_room.is_offline = true;
      Talkio.Variables.users.trigger('set_offline');
    });

    this.connect();
  },

  connect: function () {
    // Subscribing to Public channel
    this._public_subscription = this._faye_client.subscribe(this.publicChannel(), _.bind(this.processMessage, this));

    // Private channel is used for direct server communication
    this._private_subscription = this._faye_client.subscribe(this.privateChannel(), _.bind(this.processMessage, this));
  },

  disconnect: function () {
    this._faye_client.disconnect();
  },

  publicChannel: function () {
    return '/chats/' + Talkio.Variables.chat_id + '/public';
  },

  privateChannel: function (type) {
    var channel = '/chats/' + Talkio.Variables.chat_id + '/private/' + Talkio.Variables.session_token;

    if (_.isString(type)) {
      channel += '/' + type;
    }

    return channel;
  },

  publishPublicMessage: function (type, data) {
    return this._faye_client.publish(this.publicChannel(), { type: type, data: data });
  },

  publishPrivateMessage: function (type, data) {
    return this._faye_client.publish(this.privateChannel(type), data);
  },

  processMessage: function (message) {
    this._socket_notifications.process(message);
  }
});
var SocketNotifications = function () {

  this.process = function (message) {
    if (_.isString(message)) {
      message = JSON.parse(message);
    }

    // console.log(message.type);
    // console.log(message.data);

    if (typeof message != undefined && message) {
      this.execute(message.type, message.data);
    }
  };

  this.execute = function (type, data) {
    this[type](data);
  };

  this.MESSAGE = function (data) {
    Talkio.Variables.messages.add(_.defaults(data, { just_added: true }));

    var message = Talkio.Variables.messages.get(data.id);
    var user = Talkio.Variables.users.get(data.user_token);

    user.set('is_typing', false, { silent: true });
    user.trigger('new_message');

    Talkio.Variables.chat_room.softScrollToBottom();

    if (user.isCurrentUser())
      Talkio.Variables.aside.enableRemoveButton();
  };

  this.MESSAGE_DELETED = function (data) {
    Talkio.Variables.messages.markAsDeleted(data.id, data.body);
  };

  this.ALL_MESSAGES_DELETED = function (data) {
    Talkio.Variables.messages.markAsDeletedForUser(data.user_token, data.body);
  };

  this.ONLINE_PARTICIPANTS = function (data) {
    Talkio.Variables.users.reset();

    _.each(data.users, function (user) {
      Talkio.Variables.users.addUserWithData(user, { silent: true });
    });

    Talkio.Variables.online_users.render();
    Talkio.Variables.user_counter.updateCount(data.guests);
  };

  this.GUEST_JOINED = function (data) {
    Talkio.Variables.user_counter.increaseGuestsCount();
  };

  this.GUEST_LEFT = function (data) {
    Talkio.Variables.user_counter.decreaseGuestsCount();
  };

  this.USER_JOINED = function (data) {
    Talkio.Variables.users.addUserWithData(data);
  };

  this.USER_LEFT = function (data) {
    Talkio.Variables.users.removeUser(data.token);
  };

  this.USER_AUTHENTICATED = function (data) {
    Talkio.Variables.users.addUserWithData(data);
    Talkio.Variables.user_counter.decreaseGuestsCount();
  };

  this.USER_DEAUTHENTICATED = function (data) {
    Talkio.Variables.users.removeUser(data.token);
    Talkio.Variables.user_counter.increaseGuestsCount();
  };

  this.USER_IS_TYPING = function (data) {
    var user = Talkio.Variables.users.get(data.token);
    if (user) { user.setTyping(); }
  };

  this.USER_CANCELLED_TYPING = function (data) {
    var user = Talkio.Variables.users.get(data.token);
    if (user) { user.setCancelledTyping(); }
  };

  this.USER_BECAME_OWNER = function (data) {
    Talkio.Variables.owners.add(data);
  };

  this.USER_RESIGNED_OWNERSHIP = function (data) {
    Talkio.Variables.owners.remove(data.token);
  };
};
